import React, { useState } from 'react';

const Products = () => {
  // Sample categories and scents for the dropdown
  const categories = ['Candles', 'Home Fragrance'];
  const scents = ['Lavender', 'Vanilla', 'Sandalwood', 'Rose'];

  // State to manage selected category, scent, and cart
  const [selectedCategory, setSelectedCategory] = useState('');
  const [selectedScent, setSelectedScent] = useState('');
  const [cart, setCart] = useState([]);

  // Handler to add product to cart
  const handleAddToCart = () => {
    if (selectedCategory && selectedScent) {
      const product = {
        category: selectedCategory,
        scent: selectedScent
      };
      setCart([...cart, product]);
    }
  };

  return (
    <div>
      <h2>Choose your Product</h2>
      
      {/* Dropdown for Product Category */}
      <label htmlFor="category">Select Category:</label>
      <select
        id="category"
        value={selectedCategory}
        onChange={(e) => setSelectedCategory(e.target.value)}
      >
        <option value="">-- Select Category --</option>
        {categories.map((category, index) => (
          <option key={index} value={category}>
            {category}
          </option>
        ))}
      </select>

      {/* Dropdown for Scent */}
      <label htmlFor="scent">Select Scent:</label>
      <select
        id="scent"
        value={selectedScent}
        onChange={(e) => setSelectedScent(e.target.value)}
      >
        <option value="">-- Select Scent --</option>
        {scents.map((scent, index) => (
          <option key={index} value={scent}>
            {scent}
          </option>
        ))}
      </select>

      {/* Add to Cart Button */}
      <button onClick={handleAddToCart}>Add to Cart</button>

      {/* Shopping Cart Section */}
      <h3>Shopping Cart</h3>
      {cart.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <ul>
          {cart.map((product, index) => (
            <li key={index}>
              {product.category} - {product.scent}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Products;
