import React from 'react';
import './App.css';

const App = () => {
  const handleDownloadClick = () => {
    alert('Redirecting to app download page... (Replace with actual app store links)');
  };

  return (
    <div className="app">
      <header className="header">
        <nav>
          <ul>
            <li><a href="/login">Login</a></li>
            <li><a href="/register">Register</a></li>
            <li><a href="/my-account">My Account</a></li>
          </ul>
        </nav>
      </header>

      <main className="hero">
        <h1>Welcome to Velvet Lifestyle</h1>
        <p>This is the homepage of your luxury candle store.</p>
        <p>Download our app on the App Store and Google Play for exclusive offers and updates!</p>
        <button className="cta-button" onClick={handleDownloadClick}>
          Download Now
        </button>
      </main>

      <footer className="footer">
        <p>Â© 2025 Velvet Lifestyle. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default App;
